import React, { Component } from 'react'
import { productService } from '../../services/productService.js'
import './ProductDetails.scss'

export class ProductDetails extends Component {
    state = {
        product: null
    }

    async componentDidMount() {
        const { id } = this.props.match.params
        const product = await productService.getProductById(id)
        this.setState({ product })
    }

    onDeleteProduct = async () => {
        await productService.deleteProduct(this.state.product._id)
        this.props.history.push('/')
    }

    onEdit = (ev) => {
        const { _id } = this.state.product
        ev.stopPropagation()
        this.props.history.push(`/product/edit/${_id}`)
    }

    render() {
        if (!this.state.product) return <div>Loading...</div>
        const { name, email, phone } = this.state.product
        return (
            <div className="product-details">
                <div className="flex justify-center space-between">
                <i onClick={ () => this.props.history.goBack() }className="fas fa-long-arrow-alt-left"></i>
                <i onClick={this.onDeleteProduct} className="far fa-trash-alt"></i>
                </div>
                <img width="200" height="200" src={`https://robohash.org/${name}?set=set5`} alt="img"></img>
                <p>{name}</p>
                {/* <p>{email}</p>
                <p>{phone}</p> */}
                <input type="text" /><button>Transfer</button>
                <div className="flex justify-end">
                <i onClick={this.onEdit} className="far fa-edit"></i>
                </div>
            </div>
        )
    }
}
