import React from 'react'
import './ProductPreview.scss'
import { Link } from 'react-router-dom'

class _ProductPreview extends Component {
    


    generateBtns = () => {
        switch (this.props.listMode) {
          case "cartMode":
            return (<div>
              <button onClick={() => this.handleDelete(this.props.item._id)}>X</button>
            </div>
            )
          case "wishListMode":
            return (<div>
              <button onClick={() => this.handleDelete(this.props.item._id)}>X</button>
              <button onClick={() => this.handleAddToCart(this.props.item)}>Add To Cart</button>
            </div>
            )
    
          case "adminMode":
            return (
              <div>
                <button onClick={() => this.handleDelete(this.props.item._id)}>delete</button>
                <button onClick={() => this.handleEdit(this.props.item)}>edit</button>
              </div>
            )
          case "customerMode":
            return (
              <div>
              </div>
            )
    
          default:
            break;
        }
      }

      render(){

          return (
              <div className="product-preview flex column align-center">
                  <Link className="flex" to={`/product/${_id}`}>
                   <img width="200" height="200" src={imgs.img1} alt="img"></img>
                   </Link>
                   <div className="sun-name">
                   {name}
                   </div>
              </div>
          )



      }

}
