import React, { Component } from 'react'
import { contactService } from '../../services/contactService.js'
import './ProductEdit.scss'

export class ProductEdit extends Component {
    state = {
        contact: null
    }

    async componentDidMount() {
        const { id } = this.props.match.params
        const contact = id ? await contactService.getProductById(id) : await contactService.getEmptyProduct()
        this.setState({ contact })
    }

    onSaveProduct = async (ev) => {
        ev.preventDefault()
        await contactService.saveProduct(this.state.contact)
        this.props.history.push('/')
    }

    handleChange = ({ target }) => {
        const field = target.name
        const value = target.type === 'number' ? +target.value : target.value
        this.setState(({ contact }) => ({ contact: { ...contact, [field]: value } }))
    }

    render() {
        const { contact } = this.state
        if (!contact) return <div>Loading...</div>

        return (
            <form className="contact-edit" onSubmit={this.onSaveProduct}>

                <div className="flex justify-center space-between">
                    <i onClick={() => this.props.history.goBack()} className="fas fa-long-arrow-alt-left"></i>
                </div>

                {(contact._id) && <img width="200" height="200" src={`https://robohash.org/${contact.name}?set=set5`} alt="img"></img>}
                {(!contact._id) && <h2>Add New Product</h2>}
                <div className="flex column">
                    <label>Name</label>
                    <input name="name" value={contact.name} type="text" onChange={this.handleChange} placeholder="Type Your Name..." />
                    <label>Email</label>
                    <input name="email" value={contact.email} type="text" onChange={this.handleChange} placeholder="Type Your Email..." />
                    <label>Phone</label>
                    <input name="phone" value={contact.phone} type="text" onChange={this.handleChange} placeholder="Type Your Phone..." />
                </div>

                <button>Save</button>
            </form>
        )
    }
}
