import httpService from './httpService';

export const productService = {
  getProducts,
  getProductById,
  deleteProduct,
  saveProduct,
  getEmptyProduct
}

function sort(arr) {
  return arr.sort((a, b) => {
    if (a.name.toLocaleLowerCase() < b.name.toLocaleLowerCase()) {
      return -1;
    }
    if (a.name.toLocaleLowerCase() > b.name.toLocaleLowerCase()) {
