import React, { Component } from 'react'
import { connect } from 'react-redux'
import { loadContacts, setFilter } from '../../actions/ContactActions'
import { contactService } from '../../services/contactService.js'
import { ContactList } from '../../cmps/ContactList/ContactList.jsx'
import {ContactFilter} from '../../cmps/ContactFilter/ContactFilter.jsx'

class _ContactPage extends Component {

    state = {
        contacts: null,
        filterBy: null
    }

    componentDidMount() {
       this.props.loadContacts()
       this.props.setFilter()

    }

    // loadContacts = async () =>{
    //     const contacts = await contactService.getContacts(this.state.filterBy)
    //     this.setState({contacts})
    // }

    onChangeFilter = (filterBy) => {
        this.props.setFilter(filterBy)
        this.props.loadContacts()
    }


    render() {
        const {contacts} = this.props
        if (!contacts) return <div>Loading....</div>
        const contact = contacts.map((contact) => contact.name);

        return (
            <div>
                <ContactFilter onChangeFilter={ this.onChangeFilter } />
               <ContactList contacts={contacts}/>
            </div>
        )
    }
}

// gets the global state and puts it in the props of the component
function mapStateProps(state) {
    return {
        contacts: state.ContactReducer.contacts
    }
}
// Takes the action dispatchers from the actions file and puts them inside the component's props
const mapDispatchToProps = {
    loadContacts,
    setFilter
}
// Connect is used to tap into the store, without it we have no access to the store from the component
export const ContactPage = connect(mapStateProps, mapDispatchToProps)(_ContactPage)
